import React, { useRef } from 'react';
import { motion } from 'framer-motion';
import profileImage from '../Uploads/vijju.png';
import './Home.css'; 

const containerVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: {
        opacity: 1,
        y: 0,
        transition: {
            staggerChildren: 0.2,
        }
    }
};

const childVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { opacity: 1, y: 0 }
};

const continuousMotion = {
    animate: {
        y: [0, -10, 0],
        transition: {
            duration: 2,
            repeat: Infinity,
            repeatType: 'loop'
        }
    }
};

const skillsData = [
    { category: 'Programming Languages', skills: ['JavaScript', 'Java', 'Python'] },
    { category: 'Markup and Styling Languages', skills: ['HTML', 'CSS'] },
    { category: 'Frameworks and Libraries', skills: ['Node.js', 'Express.js', 'React.js', 'Redux', 'Redux-toolkit', 'Bootstrap', 'UI/UX Design'] },
    { category: 'Databases', skills: ['MongoDB', 'MySQL'] },
    { category: 'Version Control Tools', skills: ['GIT'] },
    { category: 'IDE/Tools', skills: ['Visual Studio', 'Postman', 'MongoDB Compass', 'Eclipse', 'Figma'] },
    { category: 'Operating Systems', skills: ['Windows 10'] }
];

const Home = () => {
    const aboutRef = useRef(null);
    const skillsRef = useRef(null);
    const projectsRef = useRef(null);
    const contactRef = useRef(null);

    const scrollToRef = (ref) => {
        const yOffset = -document.querySelector('header').offsetHeight; // Adjust for header height
        const y = ref.current.getBoundingClientRect().top + window.pageYOffset + yOffset;
        window.scrollTo({ top: y, behavior: 'smooth' });
    };
    

    return (
        <div>
            <header>
                <div className="name">
                    <h1>
                        MY <span className="portfolio">PORTFOLIO</span>
                    </h1>
                </div>
                <nav>
                    <ul>
                        <li><a href="#about" onClick={() => scrollToRef(aboutRef)}>About Me</a></li>
                        <li><a href="#skills" onClick={() => scrollToRef(skillsRef)}>Skills</a></li>
                        <li><a href="#projects" onClick={() => scrollToRef(projectsRef)}>Projects</a></li>
                        <li><a href="#contact" onClick={() => scrollToRef(contactRef)}>Contact</a></li>
                    </ul>
                </nav>
            </header>
            <section ref={aboutRef} id="about" className="about-section">
                <div className="about-content">
                    <div className="about-image">
                        <img src={profileImage} alt="Profile" />
                    </div>
                    <div className="about-text">
                        <motion.div
                            initial="hidden"
                            animate="visible"
                            variants={containerVariants}
                        >
                            <motion.h2 variants={childVariants}>Hello</motion.h2>
                            <motion.h2 variants={childVariants}>I am</motion.h2>
                            <motion.h2 className="highlight" variants={childVariants} {...continuousMotion}>Vijayalakshmi</motion.h2>
                            <motion.h2 variants={childVariants} {...continuousMotion}>Vardineedi</motion.h2>
                            <motion.h3
                                variants={childVariants}
                                transition={{ delay: 1.3 }}
                            >
                                A passionate developer
                            </motion.h3>
                            <motion.p
                                variants={childVariants}
                                transition={{ delay: 1.5 }}
                            >
                                I love creating interactive and dynamic user experiences.
                            </motion.p>
                        </motion.div>
                    </div>
                </div>
            </section>
            <section ref={skillsRef} id="skills" className="skills-section">
                {skillsData.map((category, index) => (
                    <div key={index}>
                        <div className="skills-category">
                            <h1>{category.category}</h1>
                        </div>
                        <div className="skills-content">
                            {category.skills.map((skill, i) => (
                                <h5 key={i}>{skill}</h5>
                            ))}
                        </div>
                    </div>
                ))}
            </section>
            <section ref={projectsRef} id="projects">
                <h2>My Projects</h2>
                <div className="project" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6 }}>
                    <h3>Project 1</h3>
                    <p>Description of project 1.</p>
                    <video controls>
                        <source src="videos/project1.mp4" type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                </div>
            </section>
            <section ref={contactRef} id="contact">
                <h2>Contact Me</h2>
                <form>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" required />
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" required />
                    <label htmlFor="message">Message:</label>
                    <textarea id="message" name="message" required></textarea>
                    <button type="submit">Send</button>
                </form>
            </section>
        </div>
    );
};

export default Home;
