import React, { useRef, useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import profileImage from '../Uploads/vijju.png';
import projectsData from './Projects';
import './Home.css';

const containerVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: {
        opacity: 1, y: 0,
        transition: {
            staggerChildren: 0.2,
        }
    }
};

const childVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { opacity: 1, y: 0 }
};

const continuousMotion = {
    animate: {
        y: [0, -10, 0],
        transition: {
            duration: 2,
            repeat: Infinity,
            repeatType: 'loop'
        }
    }
};

const Home = () => {
    const aboutRef = useRef(null);
    const skillsRef = useRef(null);
    const projectsRef = useRef(null);
    const contactRef = useRef(null);

    const scrollToRef = (ref) => {
        const yOffset = -document.querySelector('header').offsetHeight;
        const y = ref.current.getBoundingClientRect().top + window.pageYOffset + yOffset;
        window.scrollTo({ top: y, behavior: 'smooth' });
    };

    const MediaCarousel = ({ mediaSrcs }) => {
        const [currentMediaIndex, setCurrentMediaIndex] = useState(0);
        const mediaRef = useRef(null);

        useEffect(() => {
            const handleEnded = () => {
                setCurrentMediaIndex((prevIndex) => (prevIndex + 1) % mediaSrcs.length);
            };

            const mediaElement = mediaRef.current;
            if (mediaElement) {
                if (mediaElement.tagName === 'VIDEO') {
                    mediaElement.addEventListener('ended', handleEnded);
                } else {
                    const imageDuration = 3000; // 3 seconds for images
                    const timer = setTimeout(handleEnded, imageDuration);
                    return () => clearTimeout(timer);
                }
                return () => {
                    if (mediaElement.tagName === 'VIDEO') {
                        mediaElement.removeEventListener('ended', handleEnded);
                    }
                };
            }
        }, [currentMediaIndex, mediaSrcs]);

        const isVideo = mediaSrcs[currentMediaIndex].endsWith('.mp4');

        return (
            <div className="media-container">
                {isVideo ? (
                    <video
                        ref={mediaRef}
                        key={mediaSrcs[currentMediaIndex]}
                        src={mediaSrcs[currentMediaIndex]}
                        controls
                        autoPlay
                        className='media'
                    />
                ) : (
                    <img
                        ref={mediaRef}
                        key={mediaSrcs[currentMediaIndex]}
                        src={mediaSrcs[currentMediaIndex]}
                        alt={`media-${currentMediaIndex}`}
                        className='media'
                    />
                )}
            </div>
        );
    };

    return (
        <div>
            <header>
                <div className="name">
                    <h1>
                        MY <span className="portfolio">PORTFOLIO</span>
                    </h1>
                </div>
                <nav>
                    <ul>
                        <li><a href="#about" onClick={() => scrollToRef(aboutRef)}>About Me</a></li>
                        <li><a href="#skills" onClick={() => scrollToRef(skillsRef)}>Skills</a></li>
                        <li><a href="#projects" onClick={() => scrollToRef(projectsRef)}>Projects</a></li>
                        <li><a href="#contact" onClick={() => scrollToRef(contactRef)}>Contact</a></li>
                    </ul>
                </nav>
            </header>
            <section ref={aboutRef} id="about" className="about-section">
                <div className="about-content">
                    <div className="about-image">
                        <img src={profileImage} alt="Profile" />
                    </div>
                    <div className="about-text">
                        <motion.div
                            initial="hidden"
                            animate="visible"
                            variants={containerVariants}
                        >
                            <motion.h2 variants={childVariants}>Hello</motion.h2>
                            <motion.h2 variants={childVariants}>I am</motion.h2>
                            <motion.h2 className="highlight" variants={childVariants} {...continuousMotion}>Vijayalakshmi</motion.h2>
                            <motion.h2 variants={childVariants} {...continuousMotion}>Vardineedi</motion.h2>
                            <motion.h3
                                variants={childVariants}
                                transition={{ delay: 1.3 }}
                            >
                                A passionate developer
                            </motion.h3>
                            <motion.p
                                variants={childVariants}
                                transition={{ delay: 1.5 }}
                            >
                                I love creating interactive and dynamic user experiences.
                            </motion.p>
                        </motion.div>
                    </div>
                </div>
            </section>
            <section ref={skillsRef} id="skills" className="skills-section">
                <div className="skills-category">
                    <h1>Technical <span className='skills'>Skills</span></h1>
                </div>
                <div className="skills-content">
                    <h5>Programming Languages:<span className='p'>JavaScript, Java, Python</span></h5>
                    <h5>Markup and Styling Languages:<span className='p'>HTML, CSS</span></h5>
                    <h5>Frameworks and Libraries:<span className='p'>Node.js, Express.js, React.js, Redux, Redux-toolkit, Bootstrap, UI/UX Design</span></h5>
                    <h5>Databases:<span className='p'>MongoDB, MySQL</span></h5>
                    <h5>Version Control Tools:<span className='p'>GIT</span></h5>
                    <h5>IDE/Tools:<span className='p'>Visual Studio, Postman, MongoDB Compass, Eclipse, Figma</span></h5>
                    <h5>Operating Systems:<span className='p'>Windows 10</span></h5>
                </div>
            </section>
            <section ref={projectsRef} id="projects" className='projects-section'>
                <div className="projects-category">
                    <h1>MY <span className='projects'>Projects</span></h1>
                </div>
                <div className="projects-grid">
                    {projectsData.map((project, index) => (
                        <motion.div
                            key={index}
                            className="project-card"
                            whileHover={{ scale: 1.05 }}
                        >
                            <h3 className="project-title">{project.title}</h3>
                            <p className="project-description">{project.description}</p>
                            <div className="project-details">
                                <MediaCarousel mediaSrcs={project.mediaSrcs} />
                            </div>
                        </motion.div>
                    ))}
                </div>
            </section>
            <section ref={contactRef} id="contact" className="contact-section">
                <h2>Contact Me</h2>
                <form>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" name="name" required />
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" required />
                    <label htmlFor="message">Message:</label>
                    <textarea id="message" name="message" required></textarea>
                    <button type="submit">Send</button>
                </form>
            </section>
        </div>
    );
};

export default Home;
