import React from 'react';
import { Typewriter } from 'react-simple-typewriter';
import './About.css';
import profileImage from '../Uploads'; // Adjust the path as needed

const About = () => {
    return (
        <section id="about" className="about-section">
            <div className="about-content">
                <div className="about-image">
                    <img src={profileImage} alt="Profile" />
                </div>
                <div className="about-text">
                    <h2>
                        <Typewriter
                            words={['Hello', 'My Name is', 'Vijayalakshmi', 'Vardineedi']}
                            loop={false}
                            cursor
                            cursorStyle='_'
                            typeSpeed={70}
                            deleteSpeed={50}
                            delaySpeed={1000}
                        />
                    </h2>
                </div>
            </div>
        </section>
    );
};

export default About;
