import React from 'react';
import { motion } from 'framer-motion';

const ProjectItem = ({ title, description, videoSrc }) => {
    return (
        <motion.div className="project" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6 }}>
            <h3>{title}</h3>
            <p>{description}</p>
            <video controls>
                <source src={videoSrc} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        </motion.div>
    );
};

export default ProjectItem;
